async function loadPodcastEpisodes(){const e=document.getElementById("podcast-container");if(e)try{const e=await fetch("/data/blog-index.json?v="+Date.now()),n=await e.json();renderPodcastEpisodes((n.posts||[]).filter(e=>e.categories&&e.categories.includes("Talking Papers Podcast")))}catch(n){console.error("Error loading podcast episodes:",n),e.innerHTML="<p>No podcast episodes available yet.</p>"}}function renderPodcastEpisodes(e){const n=document.getElementById("podcast-container");if(!n)return;if(0===e.length)return void(n.innerHTML="<p>No podcast episodes found.</p>");const a=e.map(e=>`\n        <a href="${e.content}" class="blog-card">\n            ${e.image?`<img src="${e.image}" alt="${e.title}" class="blog-card-image" loading="lazy" width="400" height="250">`:""}\n            <div class="blog-card-content">\n                <span class="blog-category"><i class="fas fa-microphone"></i> Podcast</span>\n                <h3>${e.title}</h3>\n                <p class="blog-date">${e.date}</p>\n                <p class="blog-excerpt">${e.excerpt||""}</p>\n                <span class="read-more">Listen â†’</span>\n            </div>\n        </a>\n    `).join("");n.innerHTML=a}document.addEventListener("DOMContentLoaded",async function(){await loadPodcastEpisodes()});